package Practicejava;

public class Pattern5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//condition i must be  1
		// j must be 6
		
for (int i = 1; i<=6; i++)
			
		{ 
		for (int j = 6; j>=1; j--)
			
			if (i+j>=7) 
			
			{ System.out.print("*"); 
				}
			else {System.out.print(" ");
				
			}
		System.out.println(); 
		
		}
		

	}

}
