package Practicejava;

public class pattern6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//condition i must be  6
		// j must be 1
		
for (int i = 6; i>=1; i--)
			
		{ 
		for (int j = 1; j<=6; j++)
			
			if (j+i>=7) 
			
			{ System.out.print("*"); 
				}
			else {System.out.print(" ");
				
			}
		System.out.println(); 
		
		}
		
		
	}

}
