package Practicejava;

public class pattern7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//condition i must be  6
		// j must be 6
		
for (int i = 6; i>=1; i--)
			
		{ 
		for (int j = 6; j>=1; j--)
			
			if (j<=i) 
			
			{ System.out.print("*"); 
	
			
				}
			else {System.out.print(" ");
				
			}
		System.out.println(); 
		
		}
		
		
	}

}
